#include <fstream>
#include <vector>

using namespace std;

int main() {
  ifstream in("concurs.in");
  ofstream out("concurs.out");
  int h, n, cod;
  in >> h >> n;
  vector<int> premii(n);
  for (int i = 0; i < n; i++) {
    in >> cod;
    premii[i] = cod;
  }
  bool premiat = false;
  int nr_oras = 0, nr_scoala = 0;
  for (int i = 0; i < n; i++) {
    if (premii[i] == h) {
      premiat = true;
    }
    if (premii[i] / 100 == h / 100) {
      nr_oras++;
    }
    if (premii[i] / 10 % 10 == h / 10 % 10) {
      nr_scoala++;
    }
  }
  if (premiat) {
    nr_oras++;
    nr_scoala++;
  }
  out << (premiat ? "DA" : "NU") << endl;
  out << nr_oras << endl;
  out << nr_scoala << endl;
  return 0;
}
