#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    ifstream in("turist.in");
    ofstream out("turist.out");
    int m, n, l, c, k, x, y, max_cnt = 0;
    in >> m >> n >> l >> c >> k;
    vector<pair<int, int>> cities(k);
    for (int i = 0; i < k; i++) {
        in >> x >> y;
        cities[i] = {x, y};
    }
    // Calculate the maximum number of cities the tourist can visit in each direction
    int up = l, down = m - l - 1, left = c, right = n - c - 1;
    for (int i = 0; i < k; i++) {
        if (l == cities[i].first) {
            up = min(up, abs(c - cities[i].second));
            down = min(down, abs(c - cities[i].second));
        } else if (c == cities[i].second) {
            left = min(left, abs(l - cities[i].first));
            right = min(right, abs(l - cities[i].first));
        }
    }
    // Choose the direction with the maximum number of cities
    max_cnt = max({up, down, left, right});
    out << max_cnt << endl;
    return 0;
}
