with open('pinochio.in', 'r') as f_in, open('pinochio.out', 'w') as f_out:
    n, p, k = map(int, f_in.readline().split())
    nose_length = n
    for day in range(1, k + 1):
        if day % 7 in [1, 2, 3, 4, 5]:
            nose_length += p
        elif day % 7 == 6 or day % 7 == 0:
            nose_length -= 1
    f_out.write(str(nose_length))
