        cout << N << " " << M << endl;
    } else if (P == 3) {
        pos = 0;
        unordered_map<string, int> memo;
        cout << countEquivalentCodes(C, pos, memo) << endl;
    } else if (P == 4) {
        pos = 0;
        cout << lexicographicallyFirstCode(C, pos) << endl;
    }

    return 0;
}
