#include <iostream>
#include <fstream>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream fin("joc.in");
    std::ofstream fout("joc.out");

    int N;
    fin >> N;
    std::vector<int> v(N);
    for (int i = 0; i < N; ++i) {
        fin >> v[i];
    }

    int max_sum = 100000; // The maximum possible sum we need to check
    std::vector<bool> dp(max_sum + 1, false);
    dp[0] = true;

    for (int i = 0; i < N; ++i) {
        for (int j = max_sum; j >= v[i]; --j) {
            if (dp[j - v[i]]) {
                dp[j] = true;
            }
        }
    }

    int target_sum = 1;
    while (dp[target_sum]) {
        target_sum++;
    }

    fout << target_sum - 1 << std::endl;

    fin.close();
    fout.close();
    return 0;
}
