#include <iostream>
#include <fstream>
#include <algorithm>
#include <vector>

int main() {
    std::ifstream infile("anagrame.in");
    std::ofstream outfile("anagrame.out");

    std::string word;
    infile >> word;

    std::vector<std::string> anagrams;

    // Sort the word to start with the lexicographically smallest permutation
    std::sort(word.begin(), word.end());

    // Generate all permutations
    do {
        anagrams.push_back(word);
    } while (std::next_permutation(word.begin(), word.end()));

    // Write the anagrams to the output file
    for (const auto& anagram : anagrams) {
        outfile << anagram << "\n";
    }

    return 0;
}
