#include <iostream>
#include <fstream>
#include <algorithm>

using namespace std;

// Function to compute the greatest common divisor (GCD)
int gcd(int a, int b) {
    while (b != 0) {
        int t = b;
        b = a % b;
        a = t;
    }
    return a;
}

// Function to compute the least common multiple (LCM)
int lcm(int a, int b) {
    return a / gcd(a, b) * b;
}

