#include <iostream>
#include <fstream>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream infile("gardul.in");
    std::ofstream outfile("gardul.out");

    int n, p, q;
    infile >> n >> p >> q;

    std::vector<bool> painted(n + 1, false);
    int red_count = 0, blue_count = 0, violet_count = 0;

    for (int i = p; i <= n; i += p) {
        painted[i] = true;
        red_count++;
    }

    for (int i = q; i <= n; i += q) {
        if (painted[i]) {
            red_count--;
            violet_count++;
        } else {
            painted[i] = true;
            blue_count++;
        }
    }

    int unpainted_count = n - (red_count + blue_count + violet_count);

    outfile << unpainted_count << "\n";
    outfile << red_count << "\n";
    outfile << blue_count << "\n";
    outfile << violet_count << "\n";

    infile.close();
    outfile.close();

    return 0;
}
