#include <fstream>
#include <vector>
#include <string>

int main() {
    std::ifstream fin("sir.in");
    std::ofstream fout("sir.out");

    int n;
    fin >> n;

    std::vector<std::string> sequence = {"a", "b"};
    for (int i = 2; i < n; ++i) {
        sequence.push_back(sequence[i - 1] + sequence[i - 2]);
    }

    fout << sequence[n - 1] << std::endl;

    fin.close();
    fout.close();

    return 0;
}
