#include <fstream>
#include <stack>
#include <vector>

bool isValid(const std::vector<int>& sequence) {
    std::stack<int> s;
    for (int code : sequence) {
        if (code == 0 || code == 2) {
            s.push(code);
        } else if (code == 1) {
            if (s.empty() || s.top() != 0) {
                return false;
            }
            s.pop();
        } else if (code == 3) {
            if (s.empty() || s.top() != 2) {
                return false;
            }
            s.pop();
        }
    }
    return s.empty();
}

int main() {
    std::ifstream fin("paranteze.in");
    std::ofstream fout("paranteze.out");

    int n;
    fin >> n;

    for (int i = 0; i < n; ++i) {
        int L;
        fin >> L;

        std::vector<int> sequence(L);
        for (int j = 0; j < L; ++j) {
            fin >> sequence[j];
        }

        if (isValid(sequence)) {
            fout << "Da\n";
        } else {
            fout << "Nu\n";
        }
    }

    fin.close();
    fout.close();
    return 0;
}
