#include <fstream>
#include <vector>
#include <string>

int main() {
    std::ifstream infile("concurs.in");
    std::ofstream outfile("concurs.out");

    int H, n;
    infile >> H >> n;

    std::vector<int> codes(n);
    for (int i = 0; i < n; ++i) {
        infile >> codes[i];
    }

    int cityH = H / 100;
    int schoolH = (H / 10) % 10;

    bool isHWinner = false;
    int cityCount = 0;
    int schoolCount = 0;

    for (int i = 0; i < n; ++i) {
        if (codes[i] == H) {
            isHWinner = true;
        }
        if (codes[i] / 100 == cityH) {
            cityCount++;
            if ((codes[i] / 10) % 10 == schoolH) {
                schoolCount++;
            }
        }
    }

    outfile << (isHWinner ? "DA" : "NU") << "\n";
    outfile << cityCount << "\n";
    outfile << schoolCount << "\n";

    infile.close();
    outfile.close();

    return 0;
}
