#include <fstream>
#include <vector>
#include <string>

int main() {
    std::ifstream fin("concurs.in");
    std::ofstream fout("concurs.out");

    int H, n;
    fin >> H >> n;

    std::vector<int> codes(n);
    for (int i = 0; i < n; ++i) {
        fin >> codes[i];
    }

    // Extract city and school codes from H
    int cityH = H / 100;
    int schoolH = (H / 10) % 10;

    // Check if H is a winner
    bool isWinner = false;
    int cityCount = 0;
    int schoolCount = 0;

    for (int code : codes) {
        if (code == H) {
            isWinner = true;
        }
        int city = code / 100;
        int school = (code / 10) % 10;
        if (city == cityH) {
            ++cityCount;
        }
        if (city == cityH && school == schoolH) {
            ++schoolCount;
        }
    }

    fout << (isWinner ? "DA" : "NU") << "\n";
    fout << cityCount << "\n";
    fout << schoolCount << "\n";

    fin.close();
    fout.close();

    return 0;
}
