#include <iostream>
#include <fstream>
#include <vector>
#include <string>

int main() {
    std::ifstream inFile("concurs.in");
    std::ofstream outFile("concurs.out");

    int H;
    inFile >> H;

    int n;
    inFile >> n;

    std::vector<int> awardedCodes(n);
    for (int i = 0; i < n; ++i) {
        inFile >> awardedCodes[i];
    }

    inFile.close();

    int cityCodeH = H / 100;
    int schoolCodeH = (H / 10) % 10;

    bool isHPremiant = false;
    int cityAwards = 0;
    int schoolAwards = 0;

    for (int code : awardedCodes) {
        if (code == H) {
            isHPremiant = true;
        }
        if (code / 100 == cityCodeH) {
            cityAwards++;
            if ((code / 10) % 10 == schoolCodeH) {
                schoolAwards++;
            }
        }
    }

    outFile << (isHPremiant ? "DA" : "NU") << "\n";
    outFile << cityAwards << "\n";
    outFile << schoolAwards << "\n";

    outFile.close();

    return 0;
}
