#include <iostream>
#include <fstream>
#include <vector>
#include <queue>

using namespace std;

void floodFill(vector<vector<bool>>& board, int x, int y, int m, int p) {
    queue<pair<int, int>> q;
    q.push({x, y});
    board[x][y] = true; // Mark as visited

    // Directions for moving up, down, left, right
    int dx[] = {0, 0, 1, -1};
    int dy[] = {1, -1, 0, 0};

    while (!q.empty()) {
        int cx = q.front().first;
        int cy = q.front().second;
        q.pop();

        for (int i = 0; i < 4; ++i) {
            int nx = cx + dx[i];
            int ny = cy + dy[i];

            if (nx >= 0 && nx < m && ny >= 0 && ny < p && !board[nx][ny]) {
                board[nx][ny] = true;
                q.push({nx, ny});
            }
        }
    }
}

