    int n, m, p;
    fin >> n >> m >> p;

    vector<vector<bool>> board(m, vector<bool>(p, false));

    for (int i = 0; i < n; ++i) {
        int a, b, c, d;
        fin >> a >> b >> c >> d;
        for (int x = a; x < c; ++x) {
            for (int y = b; y < d; ++y) {
                board[x][y] = true; // Mark the cell as black
            }
        }
    }

    int whiteRegions = 0;

    for (int x = 0; x < m; ++x) {
        for (int y = 0; y < p; ++y) {
            if (!board[x][y]) {
                whiteRegions++;
                floodFill(board, x, y, m, p);
            }
        }
    }

    fout << whiteRegions << endl;

    fin.close();
    fout.close();

    return 0;
}
