#include <fstream>
#include <vector>
#include <queue>
#include <utility>

using namespace std;

const int MAX_M = 8000;
const int MAX_P = 8000;

int n, m, p;
bool grid[MAX_M][MAX_P];
bool visited[MAX_M][MAX_P];

int dx[] = {0, 0, 1, -1};
int dy[] = {1, -1, 0, 0};

void floodFill(int x, int y) {
    queue<pair<int, int>> q;
    q.push({x, y});
    visited[x][y] = true;

    while (!q.empty()) {
        auto [cx, cy] = q.front();
        q.pop();

        for (int i = 0; i < 4; ++i) {
            int nx = cx + dx[i];
            int ny = cy + dy[i];

            if (nx >= 0 && nx < m && ny >= 0 && ny < p && !grid[nx][ny] && !visited[nx][ny]) {
                visited[nx][ny] = true;
                q.push({nx, ny});
            }
        }
    }
}

int main() {
    ifstream fin("colaj.in");
    ofstream fout("colaj.out");

    fin >> n;
    fin >> m >> p;

    // Initialize the grid
    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < p; ++j) {
            grid[i][j] = false;
            visited[i][j] = false;
        }
    }

    // Read rectangles and mark them on the grid
    for (int i = 0; i < n; ++i) {
        int a, b, c, d;
        fin >> a >> b >> c >> d;
        for (int x = a; x < c; ++x) {
            for (int y = b; y < d; ++y) {
                grid[x][y] = true;
            }
        }
    }

    int whiteRegions = 0;

    // Count the number of white regions using flood fill
    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < p; ++j) {
            if (!grid[i][j] && !visited[i][j]) {
                floodFill(i, j);
                ++whiteRegions;
            }
        }
    }

    fout << whiteRegions << endl;

    fin.close();
    fout.close();

    return 0;
}
