#include <iostream>
#include <fstream>
#include <vector>
#include <cmath>
#include <algorithm>

bool isPrime(int num) {
    if (num <= 1) return false;
    if (num <= 3) return true;
    if (num % 2 == 0 || num % 3 == 0) return false;
    for (int i = 5; i * i <= num; i += 6) {
        if (num % i == 0 || num % (i + 2) == 0) return false;
    }
    return true;
}

bool isPPrime(int num, int p) {
    if (num == p) return true;
    if (num % p == 0) {
        while (num % p == 0) num /= p;
        return num == 1 || isPrime(num);
    }
    return isPrime(num);
}

int main() {
    std::ifstream infile("secvente.in");
    std::ofstream outfile("secvente.out");

    int D;
    infile >> D;

    while (D--) {
        int n, p, k;
        infile >> n >> p >> k;

        std::vector<int> sequence(n);
        for (int i = 0; i < n; ++i) {
            infile >> sequence[i];
        }

        std::vector<int> pPrimes(n);
        for (int i = 0; i < n; ++i) {
            pPrimes[i] = isPPrime(sequence[i], p);
        }

        int count = 0;
        std::vector<std::pair<int, int>> results;

        for (int start = 0; start < n; ++start) {
            if (pPrimes[start] == 0) continue;
            int pPrimeCount = 0;
            for (int end = start; end < n; ++end) {
                if (pPrimes[end]) pPrimeCount++;
                if (pPrimeCount == k && pPrimes[end]) {
                    results.emplace_back(start + 1, end + 1);
                    count++;
                }
            }
        }

        outfile << count << "\n";
        for (const auto& res : results) {
            outfile << res.first << " " << res.second << "\n";
        }
    }

    infile.close();
    outfile.close();

    return 0;
}
