#include <fstream>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream in("tablou.in");
    std::ofstream out("tablou.out");

    int p;
    in >> p;

    if (p == 1) {
        int N, K;
        in >> N >> K;

        std::vector<int> rowFlip(N + 1, 0);
        std::vector<int> colFlip(N + 1, 0);

        char type;
        int nr;
        for (int i = 0; i < K; ++i) {
            in >> type >> nr;
            if (type == 'L') {
                rowFlip[nr] ^= 1;
            } else {
                colFlip[nr] ^= 1;
            }
        }

        int positiveCount = 0;
        for (int i = 1; i <= N; ++i) {
            for (int j = 1; j <= N; ++j) {
                if ((rowFlip[i] ^ colFlip[j]) == 0) {
                    ++positiveCount;
                }
            }
        }

        out << positiveCount << "\n";
    } else if (p == 2) {
        int N, Z;
        in >> N >> Z;

        int totalCells = N * N;

        if (Z > totalCells) {
            out << 0 << "\n";
        } else {
            int halfCells = totalCells / 2;
            if (N % 2 == 0) {
                if (Z % 2 == 0) {
                    out << Z / 2 << "\n";
                } else {
                    out << 0 << "\n";
                }
            } else {
                if (Z % 2 == 0) {
                    out << Z / 2 << "\n";
                } else {
                    out << (Z + 1) / 2 << "\n";
                }
            }
        }
    }

    in.close();
    out.close();
    return 0;
}
