#include <fstream>
#include <cmath>

int main() {
    std::ifstream inFile("patrate.in");
    std::ofstream outFile("patrate.out");

    int C;
    inFile >> C;

    if (C == 1) {
        int M;
        inFile >> M;
        int K = 8 * M;
        outFile << K << std::endl;
    } else if (C == 2) {
        int N;
        inFile >> N;
        int currentNumber = 1;
        int squareNumber = 1;
        while (true) {
            int valuesInSquare = 8 * squareNumber;
            if (N <= currentNumber + valuesInSquare - 1) {
                outFile << squareNumber << std::endl;
                break;
            }
            currentNumber += valuesInSquare;
            squareNumber++;
        }
    }

    inFile.close();
    outFile.close();
    return 0;
}
