#include <fstream>
#include <vector>
#include <algorithm>

bool canLevelToHeight(const std::vector<int>& heights, int H) {
    long long soil = 0;
    for (int h : heights) {
        if (h >= H) {
            soil += h - H;
        } else {
            if (soil < H - h) {
                return false;
            }
            soil -= H - h;
        }
    }
    return true;
}

int main() {
    std::ifstream infile("buldo.in");
    std::ofstream outfile("buldo.out");

    int N;
    infile >> N;
    std::vector<int> heights(N);
    for (int i = 0; i < N; ++i) {
        infile >> heights[i];
    }

    int low = 0, high = *std::max_element(heights.begin(), heights.end());
    int bestH = 0;

    while (low <= high) {
        int mid = low + (high - low) / 2;
        if (canLevelToHeight(heights, mid)) {
            bestH = mid;
            low = mid + 1;
        } else {
            high = mid - 1;
        }
    }

    outfile << bestH << std::endl;

    return 0;
}
