#include <iostream>
#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    ifstream fin("foto.in");
    ofstream fout("foto.out");

    int C, N, M;
    fin >> C >> N >> M;

    vector<vector<int>> T(N, vector<int>(M));
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < M; ++j) {
            fin >> T[i][j];
        }
    }

    if (C == 1) {
        int maxBlack = 0;
        for (int i = 0; i < N; ++i) {
            int currentBlack = 0;
            for (int j = 0; j < M; ++j) {
                if (T[i][j] == 0) {
                    currentBlack++;
                    maxBlack = max(maxBlack, currentBlack);
                } else {
                    currentBlack = 0;
                }
            }
        }
        fout << maxBlack << endl;
    } else if (C == 2) {
        int lightningCount = 0;
        int maxHeight = 0;

        vector<vector<bool>> visited(N, vector<bool>(M, false));

        auto isValid = [&](int x, int y) {
            return x >= 0 && x < N && y >= 0 && y < M && T[x][y] == 1 && !visited[x][y];
        };

        for (int i = 0; i < N; ++i) {
            for (int j = 0; j < M; ++j) {
                if (T[i][j] == 1 && !visited[i][j]) {
                    lightningCount++;
                    int height = 0;
                    int x = i, y = j;

                    while (x < N && T[x][y] == 1 && !visited[x][y]) {
                        visited[x][y] = true;
                        height++;
                        if (isValid(x + 1, y - 1)) {
                            x = x + 1;
                            y = y - 1;
                        } else if (isValid(x + 1, y)) {
                            x = x + 1;
                        } else if (isValid(x + 1, y + 1)) {
                            x = x + 1;
                            y = y + 1;
                        } else {
                            break;
                        }
                    }
                    maxHeight = max(maxHeight, height);
                }
            }
        }
        fout << lightningCount << " " << maxHeight << endl;
    }

    fin.close();
    fout.close();
    return 0;
}
