#include <fstream>

using namespace std;

int main() {
  ifstream in("gardul.in");
  ofstream out("gardul.out");
  int n, p, q, v[40001] = {0}, i, j, k, nevopsite = 0, rosu = 0, albastru = 0, violet = 0;
  in >> n >> p >> q;
  for (i = p; i <= n; i += p) v[i] = 1;
  for (i = q; i <= n; i += q) v[i] += 2;
  for (i = 1; i <= n; i++) {
    if (v[i] == 0) nevopsite++;
    if (v[i] == 1) rosu++;
    if (v[i] == 2) albastru++;
    if (v[i] == 3) violet++;
  }
  out << nevopsite << '\n' << rosu << '\n' << albastru << '\n' << violet << '\n';
  return 0;
}
