#include <fstream>

using namespace std;

ifstream fin("cifru.in");
ofstream fout("cifru.out");

int n, v[100001], mx, mn, nr, cnt;

int main() {
  fin >> n;
  for (int i = 1; i <= n; ++i) {
    fin >> v[i];
    mx = max(mx, v[i]);
    mn = min(mn, v[i]);
  }
  fout << mx << '\n';
  for (int i = 1; i <= n; ++i) {
    if (v[i] != v[i - 1]) ++nr;
    if (v[i] != v[i + 1]) ++nr;
  }
  fout << nr << '\n';
  fout << mn << '\n';
  if (nr == 0)
    fout << 1 << '\n';
  else
    fout << 2 << '\n';
  return 0;
}
