#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  ifstream in("arme.in");
  ofstream out("arme.out");
  int n, m;
  in >> n >> m;
  vector<int> pb(n), pc(m);
  for (int i = 0; i < n; i++) in >> pb[i];
  for (int i = 0; i < m; i++) in >> pc[i];
  sort(pb.begin(), pb.end());
  sort(pc.begin(), pc.end());
  int sum = 0;
  for (int i = 0; i < n; i++) sum += max(pb[i], pc[i]);
  out << sum << endl;
  return 0;
}
