import sys

with open("livada.in", "r") as f:
    m, n, p = map(int, f.readline().split())
    grid = [list(map(int, line.split())) for line in f]

freq = [[0] * (p + 1) for _ in range(m)]
for i in range(m):
    for j in range(n):
        freq[i][grid[i][j]] += 1

max_consecutive = 0
for row in freq:
    consecutive = 0
    for el in row:
        if el > 0:
            consecutive += 1
        else:
            consecutive = 0
        max_consecutive = max(max_consecutive, consecutive)

num_rows_with_majority = 0
for row in freq:
    if max(row) > n // 2:
        num_rows_with_majority += 1

with open("livada.out", "w") as f:
    f.write(f"{num_rows_with_majority}\n")
    f.write(f"{max_consecutive}\n")

