#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

ifstream fin("ozn.in");
ofstream fout("ozn.out");

const int MAXN = 20005;
const int MAXK = 20005;
const int MAX_COORD = 2000005;

struct Segment {
    int x1, y1, x2, y2, nr;
};

struct Laser {
    int x;
};

int n, k;
Segment segments[MAXN];
Laser lasers[MAXK];
int ans[MAXK];

int main() {
    fin >> n >> k;

    for (int i = 1; i <= n; i++) {
        fin >> segments[i].x1 >> segments[i].y1 >> segments[i].x2 >> segments[i].y2 >> segments[i].nr;
    }

    for (int i = 1; i <= k; i++) {
        fin >> lasers[i].x;
    }

    for (int i = 1; i <= k; i++) {
        for (int j = 1; j <= n; j++) {
            if (segments[j].x1 <= lasers[i].x && lasers[i].x <= segments[j].x2) {
                ans[i] += segments[j].nr;
            }
        }
    }

    for (int i = 1; i <= k; i++) {
        fout << ans[i] << '\n';
    }

    fin.close();
    fout.close();
    return 0;
}
