with open('grupe.in', 'r') as f:
    n, k = map(int, f.readline().split())
    sex = f.readline()
    grupe = []
    for _ in range(k):
        grupe.append(list(map(int, f.readline().split()[1:])))

fete = [0] * k
baieti = [0] * k
for i in range(k):
    for j in grupe[i]:
        if sex[j - 1] == 'f':
            fete[i] += 1
        else:
            baieti[i] += 1

corect = True
for i in range(1, k):
    if abs(len(grupe[i]) - len(grupe[i - 1])) > 1:
        corect = False
    if abs(fete[i] - fete[i - 1]) > 1 or abs(baieti[i] - baieti[i - 1]) > 1:
        corect = False

with open('grupe.out', 'w') as f:
    for i in range(k):
        f.write(f'{baieti[i]} {fete[i]}\n')
    f.write('DA' if corect else 'NU')
