#include <fstream>
#include <stack>
#include <vector>
#include <string>

bool isValidSequence(const std::vector<int>& sequence) {
    std::stack<int> stk;
    for (int code : sequence) {
        if (code == 0 || code == 2) { // Opening brackets '0' and '2'
            stk.push(code);
        } else {
            if (stk.empty()) return false;
            int top = stk.top();
            if ((code == 1 && top == 0) || (code == 3 && top == 2)) { // Matching closing brackets '1' -> '0' and '3' -> '2'
                stk.pop();
            } else {
                return false;
            }
        }
    }
    return stk.empty();
}

int main() {
    std::ifstream infile("paranteze.in");
    std::ofstream outfile("paranteze.out");

    int n;
    infile >> n;
    for (int i = 0; i < n; ++i) {
        int L;
        infile >> L;
        std::vector<int> sequence(L);
        for (int j = 0; j < L; ++j) {
            infile >> sequence[j];
        }
        if (isValidSequence(sequence)) {
            outfile << "Da\n";
        } else {
            outfile << "Nu\n";
        }
    }

    infile.close();
    outfile.close();
    return 0;
}
