#include <fstream>
#include <vector>
#include <string>

int main() {
    std::ifstream infile("concurs.in");
    std::ofstream outfile("concurs.out");

    int H;
    infile >> H;

    int n;
    infile >> n;

    std::vector<int> codes(n);
    for (int i = 0; i < n; ++i) {
        infile >> codes[i];
    }

    infile.close();

    bool isPremiant = false;
    int cityCodeH = H / 100;
    int schoolCodeH = (H / 10) % 10;

    int cityCount = 0;
    int schoolCount = 0;

    for (int code : codes) {
        if (code == H) {
            isPremiant = true;
        }
        if (code / 100 == cityCodeH) {
            ++cityCount;
            if ((code / 10) % 10 == schoolCodeH) {
                ++schoolCount;
            }
        }
    }

    outfile << (isPremiant ? "DA" : "NU") << "\n";
    outfile << cityCount << "\n";
    outfile << schoolCount << "\n";

    outfile.close();

    return 0;
}
