#include <fstream>
#include <vector>

int main() {
    std::ifstream infile("vanatoare.in");
    std::ofstream outfile("vanatoare.out");

    long long ka, kb;
    infile >> ka >> kb;

    // Fibonacci series initialization
    std::vector<long long> fib = {1, 2};

    // Generate Fibonacci sequence until the sum of arrows runs out
    while (true) {
        long long next_fib = fib[fib.size() - 1] + fib[fib.size() - 2];
        if (next_fib > ka + kb) break;
        fib.push_back(next_fib);
    }

    int rows = 0;
    for (long long ducks : fib) {
        if (ka >= (ducks + 1) / 2 && kb >= ducks / 2) {
            ka -= (ducks + 1) / 2;
            kb -= ducks / 2;
            rows++;
        } else {
            break;
        }
    }

    outfile << rows << "\n" << ka << "\n" << kb << "\n";

    return 0;
}
