#include <iostream>
#include <fstream>
#include <vector>
#include <bitset>

using namespace std;

int countSetBits(unsigned char c) {
    return bitset<8>(c).count();
}

int main() {
    ifstream infile("paritate.in");
    ofstream outfile("paritate.out");

    string bits;
    infile >> bits;

    vector<int> error_positions;
    string decoded_message;
    int length = bits.size();

    for (int i = 0; i < length; i += 8) {
        string byte_str = bits.substr(i, 8);
        bitset<8> byte(byte_str);

        unsigned char character = byte.to_ulong();
        bool parity_bit = (character & 0x80) != 0; // Extract the leftmost bit
        character &= 0x7F; // Clear the leftmost bit

        if ((countSetBits(character) % 2 == 0 && parity_bit) || 
            (countSetBits(character) % 2 == 1 && !parity_bit)) {
            error_positions.push_back(i / 8);
        } else {
            decoded_message += character;
        }
    }

    if (error_positions.empty()) {
        outfile << "DA" << endl;
        outfile << decoded_message << endl;
    } else {
        outfile << "NU" << endl;
        for (int pos : error_positions) {
            outfile << pos << " ";
        }
        outfile << endl;
    }

    infile.close();
    outfile.close();
    return 0;
}
