#include <fstream>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream fin("joc.in");
    std::ofstream fout("joc.out");

    int N;
    fin >> N;

    std::vector<int> heaps(N);
    for (int i = 0; i < N; ++i) {
        fin >> heaps[i];
    }

    fin.close();

    std::sort(heaps.begin(), heaps.end());

    int last_sum = 0;
    for (int heap : heaps) {
        if (heap > last_sum) {
            last_sum++;
        }
    }

    fout << last_sum << '\n';
    fout.close();

    return 0;
}
