#include <fstream>
#include <algorithm>

int main() {
    std::ifstream in("anagrame.in");
    std::ofstream out("anagrame.out");

    std::string word;
    in >> word;
    in.close();

    std::sort(word.begin(), word.end());

    do {
        out << word << '\n';
    } while (std::next_permutation(word.begin(), word.end()));

    out.close();
    return 0;
}
