#include <fstream>
#include <algorithm>

int main() {
    std::ifstream fin("anagrame.in");
    std::ofstream fout("anagrame.out");
    std::string word;

    // Read word from file
    fin >> word;
    fin.close();

    // Sort the characters of the word
    std::sort(word.begin(), word.end());

    // Generate and write anagrams
    do {
        fout << word << '\n';
    } while (std::next_permutation(word.begin(), word.end()));

    fout.close();
    return 0;
}
