#include <fstream>

int main() {
    std::ifstream in("exponent.in");
    std::ofstream out("exponent.out");

    int n, k, exponent = 0;
    in >> n >> k;

    for (int i = 2; i <= n; ++i) {
        int temp = i;
        while (temp % k == 0) {
            ++exponent;
            temp /= k;
        }
    }

    out << exponent;
    return 0;
}
