#include <fstream>
#include <cmath>

int main() {
    std::ifstream fin("exponent.in");
    std::ofstream fout("exponent.out");

    int n, k;
    fin >> n >> k;

    int exponent = 0;
    for (int i = k; i <= n; i *= k) {
        exponent += n / i;
    }

    fout << exponent;

    fin.close();
    fout.close();

    return 0;
}
