#include <fstream>
#include <bitset>

int main() {
    std::ifstream in("gardul.in");
    std::ofstream out("gardul.out");

    int n, p, q, i;
    in >> n >> p >> q;

    int red = 0, blue = 0, violet = 0;
    std::bitset<100001> painted;

    // Paint red
    for (i = p; i <= n; i += p) {
        painted.set(i);
        red++;
    }

    // Paint blue and count violet
    for (i = q; i <= n; i += q) {
        if (painted[i]) {
            violet++;
        } else {
            painted.set(i);
            blue++;
        }
    }

    int unpainted = n - painted.count();

    out << unpainted << '\n';
    out << red - violet << '\n';
    out << blue << '\n';
    out << violet << '\n';

    in.close();
    out.close();

    return 0;
}
