#include <fstream>
using namespace std;

int main() {
    ifstream fin("concurs.in");
    ofstream fout("concurs.out");

    int H, n, premiant = 0, premii_oras = 0, premii_scoala = 0;
    fin >> H >> n;
    int cod_oras_H = H / 100;
    int cod_scoala_H = (H / 10) % 10;

    for (int i = 0; i < n; ++i) {
        int cod;
        fin >> cod;
        int cod_oras = cod / 100;
        int cod_scoala = (cod / 10) % 10;

        if (cod == H) {
            premiant = 1;
        }
        if (cod_oras == cod_oras_H) {
            ++premii_oras;
            if (cod_scoala == cod_scoala_H) {
                ++premii_scoala;
            }
        }
    }

    fout << (premiant ? "DA" : "NU") << '\n';
    fout << premii_oras << '\n';
    fout << premii_scoala << '\n';

    fin.close();
    fout.close();

    return 0;
}
