#include <fstream>

int main() {
    std::ifstream fin("concurs.in");
    std::ofstream fout("concurs.out");

    int H, n, city, school, student, code, city_count = 0, school_count = 0;
    bool winner = false;

    fin >> H >> n;
    city = H / 100;
    school = (H / 10) % 10;

    for (int i = 0; i < n; ++i) {
        fin >> code;

        if (code == H) winner = true;

        if (code / 100 == city) {
            city_count++;
            if ((code / 10) % 10 == school) school_count++;
        }
    }

    fout << (winner ? "DA\n" : "NU\n") << city_count << '\n' << school_count;
    fin.close();
    fout.close();

    return 0;
}
