#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    ifstream fin("siruri.in");
    ofstream fout("siruri.out");

    int n;
    fin >> n;

    vector<pair<int, int>> x(n);
    vector<int> y(n);

    for (int i = 0; i < n; ++i) {
        fin >> x[i].first;
        x[i].second = i;  // Store the original index
    }

    sort(x.begin(), x.end());

    for (int i = 0; i < n; ++i) {
        y[x[i].second] = i + 1;  // Assign the position in the sorted array to the original index
    }

    for (int i = 0; i < n; ++i) {
        fout << y[i] << " ";
    }

    fin.close();
    fout.close();
    return 0;
}
