#include <fstream>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream fin("elfi.in");
    std::vector<int> arrival_times;

    int n;
    fin >> n;

    for (int i = 0; i < n; ++i) {
        int c, p;
        fin >> c >> p;
        // Calculate all arrival times within the 4 hours period (4 * 60 * 60 = 14400 seconds)
        for (int t = c; t < 14400; t += c + p + c + 1) {
            arrival_times.push_back(t);
        }
    }

    fin.close();

    // Sort the arrival times
    std::sort(arrival_times.begin(), arrival_times.end());

    // Determine the number of faucets needed
    int faucets = 0;
    int max_faucets = 0;
    int prev_time = -1;

    for (int time : arrival_times) {
        // If the current time is the same as the previous time or one second after,
        // it means another faucet is needed.
        if (time == prev_time || time == prev_time + 1) {
            faucets++;
        } else { // Otherwise, at least one elf has left, so we free a faucet.
            faucets = 1;
        }
        max_faucets = std::max(max_faucets, faucets);
        prev_time = time;
    }

    std::ofstream fout("elfi.out");
    fout << max_faucets << '\n';
    fout.close();

    return 0;
}
