#include <fstream>
#include <queue>
#include <vector>

using namespace std;

const int dx[] = {-1, 0, 1, 0};
const int dy[] = {0, 1, 0, -1};

struct Position {
    int x, y;
};

int main() {
    ifstream fin("alee.in");
    ofstream fout("alee.out");

    int n, m, x, y, x1, y1, x2, y2;
    fin >> n >> m;
    vector<vector<int>> park(n + 1, vector<int>(n + 1, 0));

    for (int i = 0; i < m; ++i) {
        fin >> x >> y;
        park[x][y] = -1; // Marking trees
    }

    fin >> x1 >> y1 >> x2 >> y2;

    queue<Position> q;
    q.push({x1, y1});
    park[x1][y1] = 1; // Starting point

    while (!q.empty()) {
        Position p = q.front();
        q.pop();

        if (p.x == x2 && p.y == y2) {
            break; // Reached destination
        }

        for (int i = 0; i < 4; ++i) {
            int nx = p.x + dx[i];
            int ny = p.y + dy[i];

            if (nx >= 1 && nx <= n && ny >= 1 && ny <= n && park[nx][ny] == 0) {
                park[nx][ny] = park[p.x][p.y] + 1;
                q.push({nx, ny});
            }
        }
    }

    fout << park[x2][y2] << "\n"; // Output the distance as the number of tiles needed

    fin.close();
    fout.close();

    return 0;
}
