#include <fstream>
#include <unordered_map>

using namespace std;

int gcd(int a, int b) {
    while (b != 0) {
        int t = b;
        b = a % b;
        a = t;
    }
    return a;
}

int main() {
    ifstream fin("turist.in");
    ofstream fout("turist.out");

    int M, N, l, c, k, x, y, dx, dy, g;
    fin >> M >> N >> l >> c >> k;
    unordered_map<int, unordered_map<int, int>> lines;

    while (k--) {
        fin >> x >> y;
        dx = x - l;
        dy = y - c;
        g = gcd(abs(dx), abs(dy));
        dx /= g;
        dy /= g;
        lines[dx][dy]++;
    }

    int maxCities = 0;
    for (auto& line : lines) {
        for (auto& dir : line.second) {
            maxCities = max(maxCities, dir.second);
        }
    }

    fout << maxCities << endl;

    fin.close();
    fout.close();

    return 0;
}
