#include <fstream>
#include <vector>
#include <cmath>
#include <map>

using namespace std;

int main() {
    ifstream fin("cladiri.in");
    ofstream fout("cladiri.out");

    int seism_long, seism_lat, intensitate;
    fin >> seism_long >> seism_lat >> intensitate;

    int longi, lati, grad, affected_buildings = 0;
    map<int, int> levels_count; // Level, Count of buildings affected

    while (fin >> longi >> lati >> grad) {
        int distance = max(abs(seism_long - longi), abs(seism_lat - lati));
        int level_intensity = intensitate - distance;

        if (grad <= level_intensity) {
            affected_buildings++;
            levels_count[distance]++;
        }
    }
    fin.close();

    int max_buildings_per_level = 0;
    for (auto &pair : levels_count) {
        if (pair.second > max_buildings_per_level) {
            max_buildings_per_level = pair.second;
        }
    }

    fout << affected_buildings << endl;
    fout << max_buildings_per_level << endl;
    for (auto &pair : levels_count) {
        if (pair.second == max_buildings_per_level) {
            fout << pair.first << ' ';
        }
    }
    fout.close();

    return 0;
}
