#include <fstream>
#include <algorithm>
#include <cctype>

using namespace std;

int main() {
    ifstream fin("magic.in");
    ofstream fout("magic.out");

    int n;
    fin >> n;

    char magicWord[n + 1], ch;
    bool magicNumberDigits[10] = {false};
    int mwIndex = 0;

    for (int i = 0; i < n; ++i) {
        fin >> ch;
        if (isalpha(ch)) {
            magicWord[mwIndex++] = toupper(ch);
        } else if (isdigit(ch)) {
            magicNumberDigits[ch - '0'] = true;
        }
    }

    magicWord[mwIndex] = '\0';
    fout << magicWord << endl;

    // Find the smallest non-zero digit for the start of the magic number.
    for (int i = 1; i < 10; ++i) {
        if (magicNumberDigits[i]) {
            fout << i;
            magicNumberDigits[i] = false;
            break;
        }
    }

    // Write the remaining digits in ascending order.
    for (int i = 0; i < 10; ++i) {
        if (magicNumberDigits[i]) {
            fout << i;
        }
    }

    fout << endl;

    fin.close();
    fout.close();

    return 0;
}
