#include <fstream>
#include <algorithm>

int main() {
    std::ifstream fin("arme.in");
    std::ofstream fout("arme.out");

    int N, M;
    fin >> N >> M;

    int pb[1000], pc[1000];

    for (int i = 0; i < N; ++i) {
        fin >> pb[i];
    }
    for (int i = 0; i < M; ++i) {
        fin >> pc[i];
    }

    std::sort(pb, pb + N);
    std::sort(pc, pc + M, std::greater<>());

    int sum = 0;
    for (int i = 0; i < N; ++i) {
        sum += std::max(pb[i], i < M ? pc[i] : 0);
    }

    fout << sum << '\n';

    fin.close();
    fout.close();

    return 0;
}
