#include <fstream>
#include <vector>
#include <algorithm>

using namespace std;

struct UFO {
    int x1, y1, x2, y2, count;
};

int main() {
    ifstream fin("ozn.in");
    ofstream fout("ozn.out");

    int N, K;
    fin >> N >> K;
    vector<UFO> ufos(N);
    for (int i = 0; i < N; ++i) {
        fin >> ufos[i].x1 >> ufos[i].y1 >> ufos[i].x2 >> ufos[i].y2 >> ufos[i].count;
    }
    vector<int> lasers(K);
    for (int i = 0; i < K; ++i) {
        fin >> lasers[i];
    }
    fin.close();

    // Initialize the result vector with zeros
    vector<int> result(K, 0);

    // Check each laser position
    for (int i = 0; i < K; ++i) {
        // Check each UFO
        for (const auto& ufo : ufos) {
            // Check if the laser intersects the UFO
            if (lasers[i] >= ufo.x1 && lasers[i] <= ufo.x2) {
                // Increase the count of destroyed aliens for the current laser
                result[i] += ufo.count;
            }
        }
    }

    // Output the result
    for (int i = 0; i < K; ++i) {
        fout << result[i] << '\n';
    }

    fout.close();
    return 0;
}
