#include <fstream>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream fin("buldo.in");
    std::ofstream fout("buldo.out");

    int N;
    fin >> N;

    std::vector<long long> heights(N);
    for (int i = 0; i < N; ++i) {
        fin >> heights[i];
    }

    long long sum = 0;
    for (auto height : heights) {
        sum += height;
    }

    long long H = sum / N; // Start with the average height
    long long dirt = 0;
    bool valid;

    do {
        valid = true;
        dirt = 0;

        for (int i = 0; i < N; ++i) {
            if (heights[i] >= H) {
                dirt += heights[i] - H;
            } else {
                if (dirt >= H - heights[i]) {
                    dirt -= H - heights[i];
                } else {
                    valid = false;
                    break;
                }
            }
        }

        // If not valid, decrease H and try again
        if (!valid) --H;

    } while (!valid);

    fout << H;
    fout.close();
    fin.close();

    return 0;
}
