#include <fstream>

int main() {
    std::ifstream fin("cartonase.in");
    std::ofstream fout("cartonase.out");

    int c, n;
    fin >> c >> n;

    int s, d, prev_d = -1;
    int pairs = 0, longest_seq = 0, current_seq = 0, max_seq_count = 0;

    for (int i = 0; i < n; ++i) {
        fin >> s >> d;

        if (s == prev_d) {
            pairs++;           // Count matching pairs
            current_seq++;     // Increase length of current sequence
        } else {
            current_seq = 1;   // Reset sequence length
        }

        if (current_seq > longest_seq) {
            longest_seq = current_seq;  // Update longest sequence
            max_seq_count = 1;          // Reset sequence count
        } else if (current_seq == longest_seq) {
            max_seq_count++;            // Increment sequence count
        }

        prev_d = d;            // Update previous right number
    }

    if (c == 1) {
        fout << pairs;         // Output number of matching pairs
    } else if (c == 2) {
        fout << longest_seq;   // Output length of the longest sequence
    } else if (c == 3) {
        fout << max_seq_count; // Output count of longest sequences
    }

    fin.close();
    fout.close();

    return 0;
}
