def main():
    with open('joc.in', 'r') as fin:
        N = int(fin.readline().strip())
        heaps = list(map(int, fin.readline().split()))

    total = 0
    for stones in heaps:
        if total + 1 >= stones:
            total += stones

    with open('joc.out', 'w') as fout:
        fout.write(str(total) + '\n')

if __name__ == "__main__":
    main()
